mapper.insert:
insert
	into ad
	(
		AD_NO
	,	AD_TITLE
	,	AD_LINK
	,	ORIGIN_NAME
	,	CHANGE_NAME
	)
	values
	(
		seq_fno.nextval
	  , #{adTitle}
	  , #{adLink}
	  , #{originName}
	  , #{changeName}
  )

mapper.update:
update
		ad
	set	ad_title = #{adTitle}
	  ,	ad_link	= #{adLink}
	  , origin_name = #{originName}
	  , change_name = #{changeName}
  where	ad_no = #{adNo}
