mapper.update:
update
	   admin_board
   set board_title = #{boardTitle}
     , board_content = #{boardContent}
 where board_no = #{boardNo}

mapper.insert:
insert
  into admin_board
  (
    board_no
  , board_title
  , user_no
  , board_content
  , board_type
  , faq_category
  )
  values
  (
    seq_nno.nextval
  , #{boardTitle}
  , #{userNo}
  , #{boardContent}
  , 'FAQ'
  , #{faqCategory}
  )

